SELECT  wl.*, t.tour_img1_path, t.tour_name
FROM    wishlist wl
JOIN    tour t ON wl.tour_num = t.tour_num
WHERE   wl.user_id = #{userId}
ORDER   BY wl.wishlist_num DESC
LIMIT   #{limit} OFFSET #{offset}

